import{_ as a,c as o,a1 as l,o as i}from"./chunks/framework.S1oRGgk6.js";const m=JSON.parse('{"title":"Packages","description":"","frontmatter":{"outline":"deep","head":[["meta",{"name":"og:title","content":"Packages | ProzillaOS Docs"}],["meta",{"name":"twitter:title","content":"Packages | ProzillaOS Docs"}],["meta",{"name":"og:description","content":"Documentation for ProzillaOS and its packages."}],["meta",{"name":"twitter:description","content":"Documentation for ProzillaOS and its packages."}],["link",{"rel":"canonical","href":"https://os.prozilla.dev/docs/reference/packages"}],["meta",{"name":"og:url","content":"https://os.prozilla.dev/docs/reference/packages"}],["meta",{"name":"twitter:url","content":"https://os.prozilla.dev/docs/reference/packages"}],["meta",{"name":"og:locale","content":"en_US"}],["meta",{"name":"og:image","content":"https://os.prozilla.dev/docs/prozilla-os-title-banner.png"}],["meta",{"name":"twitter:image","content":"https://os.prozilla.dev/docs/prozilla-os-title-banner.png"}],["meta",{"name":"og:type","content":"website"}],["meta",{"name":"twitter:card","content":"summary_large_image"}]]},"headers":[],"relativePath":"reference/packages.md","filePath":"reference/packages.md"}'),r={name:"reference/packages.md"};function t(s,e,n,p,c,d){return i(),o("div",null,e[0]||(e[0]=[l('<h1 id="packages" tabindex="-1">Packages <a class="header-anchor" href="#packages" aria-label="Permalink to &quot;Packages&quot;">​</a></h1><p>This is an overview of all packages related to ProzillaOS. Each package is published to the npm registry.</p><h2 id="bundle" tabindex="-1">Bundle <a class="header-anchor" href="#bundle" aria-label="Permalink to &quot;Bundle&quot;">​</a></h2><p>This bundle lets you quickly get started with ProzillaOS without the hassle of installing all packages individually.</p><ul><li><a href="./prozilla-os/"><code>prozilla-os</code></a>: A bundle containing <code>@prozilla-os/core</code> and all primary applications of ProzillaOS</li></ul><h2 id="libraries" tabindex="-1">Libraries <a class="header-anchor" href="#libraries" aria-label="Permalink to &quot;Libraries&quot;">​</a></h2><p>These libraries are different modules of ProzillaOS that can be installed separately or via the bundle package <code>prozilla-os</code>.</p><ul><li><a href="./core/"><code>@prozilla-os/core</code></a>: Core functionality, React components and hooks</li><li><a href="./skins/"><code>@prozilla-os/skins</code></a>: Customizable skins for ProzillaOS</li><li><a href="./shared/"><code>@prozilla-os/shared</code></a>: Shared functions and utilities</li><li><a href="./dev-tools/"><code>@prozilla-os/dev-tools</code></a>: Tools for development of ProzillaOS packages</li></ul><h2 id="applications" tabindex="-1">Applications <a class="header-anchor" href="#applications" aria-label="Permalink to &quot;Applications&quot;">​</a></h2><h3 id="primary-applications" tabindex="-1">Primary applications <a class="header-anchor" href="#primary-applications" aria-label="Permalink to &quot;Primary applications&quot;">​</a></h3><p>These applications are included in the <code>prozilla-os</code> bundle.</p><ul><li><a href="./apps/file-explorer/"><code>@prozilla-os/file-explorer</code></a>: File explorer app</li><li><a href="./apps/terminal/"><code>@prozilla-os/terminal</code></a>: Terminal/shell app</li><li><a href="./apps/text-editor/"><code>@prozilla-os/text-editor</code></a>: Text editor app</li><li><a href="./apps/settings/"><code>@prozilla-os/settings</code></a>: Settings app</li><li><a href="./apps/media-viewer/"><code>@prozilla-os/media-viewer</code></a>: Media viewer app</li><li><a href="./apps/browser/"><code>@prozilla-os/browser</code></a>: Browser app</li><li><a href="./apps/calculator/"><code>@prozilla-os/calculator</code></a>: Calculator app</li></ul><h3 id="secondary-applications" tabindex="-1">Secondary applications <a class="header-anchor" href="#secondary-applications" aria-label="Permalink to &quot;Secondary applications&quot;">​</a></h3><ul><li><a href="./apps/logic-sim/"><code>@prozilla-os/logic-sim</code></a>: Logic simulator app</li></ul><h3 id="external-applications" tabindex="-1">External applications <a class="header-anchor" href="#external-applications" aria-label="Permalink to &quot;External applications&quot;">​</a></h3><p>These applications are related to ProzillaOS and used in the demo, but their source code is hosted in a separate repository on GitHub.</p><ul><li><a href="https://github.com/prozilla-os/ProzillaOS-games/blob/main/packages/games/minesweeper" target="_blank" rel="noreferrer"><code>@prozilla-os/minesweeper</code></a>: Minesweeper clone</li><li><a href="https://github.com/prozilla-os/ProzillaOS-games/blob/main/packages/games/wordle" target="_blank" rel="noreferrer"><code>@prozilla-os/wordle</code></a>: Wordle clone</li><li><a href="https://github.com/prozilla-os/ProzillaOS-games/blob/main/packages/games/ball-maze" target="_blank" rel="noreferrer"><code>@prozilla-os/ball-maze</code></a>: 3D wooden ball maze game</li><li><a href="https://github.com/prozilla-os/Bliss-radio-app" target="_blank" rel="noreferrer"><code>@prozilla-os/bliss-radio</code></a>: Radio app</li></ul>',17)]))}const u=a(r,[["render",t]]);export{m as __pageData,u as default};
